input_full.script:
// Check for MathJax elements, Lean code blocks, and LaTeX content
const mathJax = document.querySelectorAll('.MathJax, mjx-container, .katex');
const leanStatements = document.querySelectorAll('.hl.lean');
const leanProofs = document.querySelectorAll('.sbs-lean-proof');
const sbsLatexColumns = document.querySelectorAll('.sbs-latex-column');

// Check if any .sbs-latex-column has grid/flex with 2 columns
let hasTwoColumns = false;
sbsLatexColumns.forEach(col => {
  const style = window.getComputedStyle(col);
  const gridCols = style.gridTemplateColumns;
  if (gridCols && gridCols.split(' ').length >= 2) hasTwoColumns = true;
});

// Check for actual proof content (not empty)
let nonEmptyProofs = 0;
leanProofs.forEach(p => {
  if (p.textContent.trim().length > 10) nonEmptyProofs++;
});

({
  math_rendered: mathJax.length > 0,
  math_count: mathJax.length,
  lean_statement_count: leanStatements.length,
  lean_proof_count: leanProofs.length,
  non_empty_proofs: nonEmptyProofs,
  sbs_latex_columns: sbsLatexColumns.length,
  has_two_column_layout: hasTwoColumns
})

