\documentclass{article}
\usepackage{amsmath,amssymb,amsthm}
\usepackage{hyperref}

\title{Reductive Groups over Fields: A Formalization Blueprint}
\author{SBS Showcase}
\date{}

\theoremstyle{definition}
\newtheorem{definition}{Definition}[section]
\newtheorem{theorem}[definition]{Theorem}
\newtheorem{lemma}[definition]{Lemma}
\newtheorem{proposition}[definition]{Proposition}
\newtheorem{example}[definition]{Example}

\begin{document}
\maketitle

\begin{abstract}
This blueprint outlines the formalization path from mathlib foundations to the definition 
of connected reductive groups over a field. Following Brian Conrad's approach, we define 
a connected affine algebraic group $G$ over a field $k$ to be \textbf{reductive} if it is 
smooth over $k$ and the unipotent radical of $G_{\bar{k}}$ is trivial.
\end{abstract}

\tableofcontents

\section{Foundation: Affine Group Schemes}

\begin{definition}[Affine Group Scheme of Finite Type]
\label{def:affine-group-scheme}
An \textbf{affine group scheme of finite type} over a field $k$ is a group object 
in the category of affine schemes of finite type over $\operatorname{Spec} k$.
\end{definition}

\begin{definition}[Hopf Algebra Approach]
\label{def:hopf-approach}
For affine groups over a field, the coordinate ring $k[G]$ carries a Hopf algebra structure:
\begin{itemize}
  \item Comultiplication $\Delta: k[G] \to k[G] \otimes_k k[G]$ (group multiplication)
  \item Counit $\varepsilon: k[G] \to k$ (identity element)
  \item Antipode $S: k[G] \to k[G]$ (inverse)
\end{itemize}
\end{definition}

\begin{definition}[Scheme Approach]
\label{def:scheme-approach}
Using categorical language, an affine group scheme is a group object in the category 
$\mathbf{Aff}_k$ of affine schemes over $k$, represented via the functor of points.
\end{definition}

\section{Smooth Group Schemes}

\begin{definition}[Smooth Morphism]
\label{def:smooth}
A morphism of schemes $f: X \to Y$ is \textbf{smooth} if it is locally of finite 
presentation, flat, and has geometrically regular fibers.
\end{definition}

\begin{theorem}[Cartier's Theorem]
\label{thm:cartier}
Over a field of characteristic zero, every algebraic group is smooth.
\end{theorem}

\section{Unipotent Theory}

\begin{definition}[Unipotent Element]
\label{def:unipotent-element}
An element $g$ of a linear algebraic group $G \subseteq \mathrm{GL}_n$ is 
\textbf{unipotent} if $(g - I)^n = 0$, i.e., $g$ is a unipotent matrix.
\end{definition}

\begin{definition}[Unipotent Group]
\label{def:unipotent-group}
A linear algebraic group $U$ is \textbf{unipotent} if every element is unipotent, 
equivalently if $U$ admits a composition series with successive quotients 
isomorphic to $\mathbb{G}_a$.
\end{definition}

\begin{definition}[Unipotent Radical]
\label{def:unipotent-radical}
The \textbf{unipotent radical} $R_u(G)$ of a connected affine algebraic group $G$ 
is the maximal smooth connected unipotent normal closed subgroup of $G$.
\end{definition}

\begin{proposition}[Existence of Unipotent Radical]
\label{prop:radical-exists}
For any connected affine algebraic group $G$ over a field $k$, the unipotent 
radical $R_u(G)$ exists and is unique.
\end{proposition}

\section{Reductive Groups}

\begin{definition}[Reductive Group]
\label{def:reductive}
A connected affine algebraic group $G$ over a field $k$ is \textbf{reductive} if:
\begin{enumerate}
  \item $G$ is smooth over $k$, and
  \item $R_u(G_{\bar{k}}) = 1$, where $G_{\bar{k}} = G \times_k \bar{k}$.
\end{enumerate}
Equivalently, $G_{\bar{k}}$ has no nontrivial smooth connected unipotent normal 
closed $\bar{k}$-subgroup.
\end{definition}

\begin{example}[General Linear Group]
\label{ex:gl}
$\mathrm{GL}_n$ is reductive: it is smooth and has trivial unipotent radical.
\end{example}

\begin{example}[Special Linear Group]
\label{ex:sl}
$\mathrm{SL}_n$ is reductive.
\end{example}

\begin{example}[Orthogonal and Symplectic Groups]
\label{ex:classical}
$\mathrm{SO}_n$ and $\mathrm{Sp}_{2n}$ are reductive.
\end{example}

\section{Structure Theory}

\begin{definition}[Torus]
\label{def:torus}
A \textbf{torus} over $k$ is an algebraic group $T$ such that $T_{\bar{k}} \cong 
\mathbb{G}_m^n$ for some $n$.
\end{definition}

\begin{definition}[Maximal Torus]
\label{def:maximal-torus}
A \textbf{maximal torus} of a reductive group $G$ is a torus $T \subseteq G$ 
not properly contained in any other torus.
\end{definition}

\begin{definition}[Borel Subgroup]
\label{def:borel}
A \textbf{Borel subgroup} of a connected reductive group $G$ over an algebraically 
closed field is a maximal connected solvable closed subgroup.
\end{definition}

\begin{definition}[Split Reductive Group]
\label{def:split}
A reductive group $G$ over $k$ is \textbf{split} if it contains a maximal torus 
that is split (isomorphic to $\mathbb{G}_m^n$ over $k$ itself).
\end{definition}

