/** Renders text with inline LaTeX math ($...$) using KaTeX with MathML output.
 *  MathML is natively supported by Chromium (VSCode webview) and needs no external CSS. */
function MathStatement({ text }: { text: string }) {
    const parts = text.split(/(\$[^$]+\$)/g)
    return (
        <span>
            {parts.map((part, i) => {
                if (part.startsWith('$') && part.endsWith('$')) {
                    const expr = part.slice(1, -1)
                    try {
                        const html = katex.renderToString(expr, { output: 'mathml', throwOnError: false })
                        return <span key={i} dangerouslySetInnerHTML={{ __html: html }} />
                    } catch {
                        return <span key={i}>{part}</span>
                    }
                }
                return <React.Fragment key={i}>{part}</React.Fragment>
            })}
        </span>
    )
}

