"claudeCode.useCtrlEnterToSend": true,
    "githubIssues.queries": [
        {
            "label": "SBS Open Issues",
            "query": "is:open repo:e-vergo/Side-By-Side-Blueprint"
        },
        {
            "label": "SBS Bugs",
            "query": "is:open label:bug repo:e-vergo/Side-By-Side-Blueprint"
        },
        {
            "label": "SBS Features",
            "query": "is:open label:feature repo:e-vergo/Side-By-Side-Blueprint"
        },
        {
            "label": "My Issues",
            "query": "is:open assignee:@me"
        }
    ],
    "githubIssues.issueCompletionFormatScm": "${issueNumberLabel}: ${issueTitle}",
    "githubIssues.createIssueTriggers": ["TODO", "FIXME", "BUG"]
}
