(function() {
    const results = {};
    // Check for stats/progress section using actual content
    const allText = document.body.innerText;
    results.hasProgress = allText.includes('Progress') || allText.includes('not ready') || allText.includes('notReady');
    results.hasKeyDeclarations = allText.includes('Key Declarations') || allText.includes('Key Theorems');
    results.hasProjectNotes = allText.includes('Project Notes') || allText.includes('Messages');
    results.hasChecks = allText.includes('Checks');

    // Check for 6 status colors in the progress section
    // Look for status color indicators
    const statusTexts = ['not ready', 'ready', 'sorry', 'proven', 'fully proven', 'mathlib ready', 
                         'notReady', 'ready', 'sorry', 'proven', 'fullyProven', 'mathlibReady'];
    results.statusTextsFound = statusTexts.filter(s => allText.toLowerCase().includes(s.toLowerCase()));

    // Check layout - is it 2-column?
    const mainContent = document.querySelector('.dashboard-grid, .dashboard-content, .content-area, main');
    if (mainContent) {
        const style = getComputedStyle(mainContent);
        results.mainDisplay = style.display;
        results.mainGridCols = style.gridTemplateColumns;
    }

    // Check for the grid container directly
    const allDivs = document.querySelectorAll('div');
    for (const div of allDivs) {
        const style = getComputedStyle(div);
        if (style.display === 'grid' && style.gridTemplateColumns.split(' ').length >= 2) {
            results.gridContainerFound = true;
            results.gridColValue = style.gridTemplateColumns;
            break;
        }
    }

    return JSON.stringify(results);
})()

