(function() {
    const results = {};

    // Check legend with 6 status colors
    const legendItems = document.querySelectorAll('.legend-item, .graph-legend-item, [class*="legend"]');
    results.legendItemCount = legendItems.length;
    results.legendTexts = Array.from(legendItems).map(el => el.textContent.trim()).slice(0, 10);

    // Check SVG status node colors
    const svgNodes = document.querySelectorAll('.node rect, .node circle, .node ellipse, .node polygon');
    const nodeColors = new Set();
    svgNodes.forEach(n => {
        const fill = n.getAttribute('fill') || getComputedStyle(n).fill;
        if (fill) nodeColors.add(fill);
    });
    results.uniqueNodeColors = Array.from(nodeColors);

    // Expected colors
    const expected = {
        notReady: '#F4A460', ready: '#20B2AA', sorry: '#8B0000',
        proven: '#90EE90', fullyProven: '#228B22', mathlibReady: '#87CEEB'
    };

    // Check each expected color
    results.colorMatches = {};
    for (const [status, hex] of Object.entries(expected)) {
        const hexLower = hex.toLowerCase();
        results.colorMatches[status] = Array.from(nodeColors).some(c => 
            c.toLowerCase() === hexLower || c.toLowerCase().includes(hexLower)
        );
    }

    // Check for SVG viewBox
    const svg = document.querySelector('svg');
    if (svg) {
        results.viewBox = svg.getAttribute('viewBox');
        results.svgPresent = true;
    } else {
        results.svgPresent = false;
    }

    // Graph centered check
    results.graphCentered = !!svg;

    // Zoom controls
    const zoomIn = document.querySelector('#graph-zoom-in');
    const zoomOut = document.querySelector('#graph-zoom-out');
    const zoomFit = document.querySelector('#graph-fit');
    results.zoomInPresent = !!zoomIn;
    results.zoomOutPresent = !!zoomOut;
    results.zoomFitPresent = !!zoomFit;

    // Pan controls
    const panControls = document.querySelectorAll('[class*="pan"], #graph-pan-left, #graph-pan-right, #graph-pan-up, #graph-pan-down');
    results.panControlsCount = panControls.length;

    // Nodes clickable
    const nodes = document.querySelectorAll('.node');
    results.nodeCount = nodes.length;

    // Edges
    const edges = document.querySelectorAll('.edge, line, path.edge');
    results.edgeCount = edges.length;

    // Overflow check
    results.hasOverflow = document.documentElement.scrollWidth > document.documentElement.clientWidth;

    // Sidebar
    results.sidebarPresent = !!document.querySelector('.sidebar, nav, #sidebar');
    results.activeHighlight = !!document.querySelector('.active, .sidebar-active');

    // Theme toggle
    results.themeToggle = !!document.querySelector('#theme-toggle, .theme-toggle');

    return JSON.stringify(results);
})()

