python3 -c "
import json
with open('/Users/eric/GitHub/Side-By-Side-Blueprint/dev/storage/archive_index.json') as f:
    data = json.load(f)

entries = data['entries']
# Build ordered list of transitions
transitions = []
for eid, e in sorted(entries.items(), key=lambda x: x[1].get('created_at', '')):
    gs = e.get('global_state')
    st = e.get('state_transition')
    if st:
        transitions.append({
            'id': eid,
            'global_state': gs,
            'state_transition': st,
            'created_at': e.get('created_at', '')[:19]
        })

# Parse into skill sessions
sessions = []
current_session = None
for t in transitions:
    if t['state_transition'] == 'phase_start' and (current_session is None or current_session.get('skill') != (t['global_state'] or {}).get('skill')):
        # New skill starting
        if current_session:
            sessions.append(current_session)
        current_session = {
            'skill': (t['global_state'] or {}).get('skill'),
            'start_id': t['id'],
            'start_time': t['created_at'],
            'phases': [(t['global_state'] or {}).get('substate')],
            'ended_cleanly': False,
            'end_id': None,
            'end_time': None
        }
    elif t['state_transition'] == 'phase_start' and current_session:
        current_session['phases'].append((t['global_state'] or {}).get('substate'))
    elif t['state_transition'] == 'phase_end':
        if current_session:
            current_session['ended_cleanly'] = True
            current_session['end_id'] = t['id']
            current_session['end_time'] = t['created_at']
            sessions.append(current_session)
            current_session = None

if current_session:
    sessions.append(current_session)

# Analyze by skill
from collections import defaultdict
stats = defaultdict(lambda: {'total': 0, 'completed': 0, 'incomplete_last_phase': [], 'complete_phases': []})
for s in sessions:
    skill = s['skill']
    stats[skill]['total'] += 1
    if s['ended_cleanly']:
        stats[skill]['completed'] += 1
        stats[skill]['complete_phases'].append(s['phases'])
    else:
        stats[skill]['incomplete_last_phase'].append(s['phases'][-1] if s['phases'] else 'unknown')

print('=== SKILL SESSION ANALYSIS ===')
for skill, st in stats.items():
    print(f'\n{skill}: {st[\"completed\"]}/{st[\"total\"]} completed ({100*st[\"completed\"]//max(st[\"total\"],1)}%)')
    if st['incomplete_last_phase']:
        from collections import Counter
        c = Counter(st['incomplete_last_phase'])
        print(f'  Incomplete sessions died in:')
        for phase, count in c.most_common():
            print(f'    {phase}: {count}')
    if st['complete_phases']:
        print(f'  Complete session phase sequences:')
        for phases in st['complete_phases'][-3:]:
            print(f'    {\" -> \".join(phases)}')
"
