input_full.script:
(() => {
    const boxes = document.querySelectorAll('.messagebox');
    return Array.from(boxes).map((box, i) => {
        const senderEl = box.querySelector('.message-header-wrapper .message_label_clickable') || box.querySelector('.sender_name');
        const sender = senderEl ? senderEl.textContent.trim() : 'Unknown';
        const content = box.querySelector('.message_content.rendered_markdown');
        const text = content ? content.innerText.trim() : '';
        return `[${i+1}] ${sender}:\n${text}`;
    }).join('\n\n---\n\n');
})()
