(() => {
  const results = {};

  // Get the progress panel HTML to understand structure
  const progressSection = document.querySelector('.progress-section, .stats-panel');
  if (!progressSection) {
    // Try to find by text content
    const allSections = document.querySelectorAll('.dashboard-grid section, .dashboard-grid .panel, .dashboard-grid > div > div');
    for (const s of allSections) {
      const h = s.querySelector('h2, h3');
      if (h && h.textContent.includes('Progress')) {
        results.progressHTML = s.innerHTML.substring(0, 500);
        break;
      }
    }
  }

  // Get all text nodes mentioning status names
  const body = document.body.textContent;
  results.hasNotReady = body.includes('not ready') || body.includes('notReady');
  results.hasReady = body.includes(' ready');
  results.hasSorry = body.includes('sorry');
  results.hasProven = body.includes('proven');
  results.hasFullyProven = body.includes('fully proven') || body.includes('fullyProven');
  results.hasMathlibReady = body.includes('mathlib') || body.includes('mathlibReady');

  // Get all unique background colors from status dots
  const dots = document.querySelectorAll('.status-dot');
  const uniqueColors = new Set();
  dots.forEach(d => {
    const style = d.getAttribute('style');
    if (style) uniqueColors.add(style);
  });
  results.uniqueStatusColors = [...uniqueColors];

  // Get the dashboard row structure
  const rows = document.querySelectorAll('.dashboard-row');
  results.rowCount = rows.length;
  results.rowDetails = [];
  rows.forEach(row => {
    const children = row.children;
    const childDetails = [];
    for (const ch of children) {
      const heading = ch.querySelector('h2, h3');
      childDetails.push({
        cls: ch.className.substring(0, 40),
        heading: heading ? heading.textContent.trim() : null,
        width: getComputedStyle(ch).width
      });
    }
    results.rowDetails.push({cls: row.className, childCount: children.length, children: childDetails});
  });

  return JSON.stringify(results);
})()

