    const rect = graphContainer.getBoundingClientRect();
    const viewportWidth = window.innerWidth;
    const sidebarWidth = document.querySelector('.sidebar, nav')?.getBoundingClientRect().width || 0;
    const availableWidth = viewportWidth - sidebarWidth;
    results.graphLeft = rect.left;
    results.graphWidth = rect.width;
    results.availableWidth = availableWidth;
    results.isCentered = Math.abs((rect.left - sidebarWidth) + rect.width/2 - (sidebarWidth + availableWidth/2)) < 50;
  }

  return JSON.stringify(results);
})()

